import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });
            const token = response.data.token;
            localStorage.setItem('token', token); // Save token to localStorage
            console.log("localstorage token data", token);

            // Decode the token to get user info
            const decoded: any = jwt_decode(token);
            console.log('Decoded token:', decoded); // Decoded token data

            navigate('/'); // Navigate after successful login
        } catch (error) {
            console.error('Login failed:', error);
        }
    };

    useEffect(() => {
        // Check if a token is available in localStorage and decode it
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decoded: any = jwt_decode(token);
                console.log('Decoded token from localStorage:', decoded);
            } catch (error) {
                console.error('Error decoding token from localStorage:', error);
            }
        }
    }, []); // This will run once when the component mounts

    return (
        <Container maxWidth="sm">
            <Typography variant="h4">Login</Typography>
            <form onSubmit={handleLogin}>
                <TextField
                    label="Email"
                    fullWidth
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    margin="normal"
                />
                <TextField
                    label="Password"
                    type="password"
                    fullWidth
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    margin="normal"
                />
                <Button type="submit" variant="contained" color="primary">Login</Button>
            </form>
        </Container>
    );
};

export default Login;
